const Child = (props) => {
    return (
        <div className='bg-red border w-75 mt-4 border-dark rounded  p-5 d-flex flex-column justify-content-center align-items-center'>
            <h3>Child Div</h3>
            <p>Content from <b>Parent</b> Div: <br/>{props.fromParent}</p>
            <div className='form-group'>
                <label className='form-label' for="parent">Type here to send data to parent:</label>
                <input id="parent" className="form-control" type='text' onChange={(e) => props.updateParent(e.target.value)}></input>
            </div>
        </div>
    )
};

export default Child;
