import { useState } from 'react';
import Child from './Child';

const Parent = () => {
    const [childData, setChildData] = useState("");
    const [sendData, setSendData] = useState("");

    function updateChildData(data) {
        setChildData(data)
    }   

    function childText(data) {
        setSendData(data);
    }

    return (
        <div style={{height: "100vh"}} className='w-100 d-flex flex-column justify-content-center align-items-center'>
            <div className='border border-dark rounded  p-5 d-flex flex-column justify-content-center align-items-center'>
                <h3>Parent Div</h3>
                <p>Content from Child Div: {childData}</p>
                <Child updateParent={updateChildData} fromParent={sendData} />
                <div className='form-group'>
                    <label></label>
                    <input className="form-control" type='text' onChange={(e) => childText(e.target.value)}></input>
                </div>
                
            </div>
        </div>
    );  
};

export default Parent;
