// src/Todo.jimport React, { useState } from 'react';

const Todo = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const addTask = () => {
    if (newTask.trim() !== '') {
      setTasks([...tasks, { text: newTask, completed: false }]);
      setNewTask('');
    }
  };

  const toggleTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };

  const removeTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  const removeAllTasks = () => {
    setTasks([]);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Todo List</h1>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Add a new task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button className="btn btn-primary" type="button" onClick={addTask}>
          Add
        </button>
      </div>
      <ul className="list-group">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.completed ? 'text-muted' : ''
            }`}
          >
            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                id={`checkbox-${index}`}
                checked={task.completed}
                onChange={() => toggleTask(index)}
              />
              <label className="form-check-label" htmlFor={`checkbox-${index}`}>
                {task.completed ? <del>{task.text}</del> : task.text}
              </label>
            </div>
            <button className="btn btn-danger btn-sm" onClick={() => removeTask(index)}>
              Remove
            </button>
          </li>
        ))}
      </ul>
      {tasks.length > 0 && (
        <div className="mt-3">
          <button className="btn btn-danger" onClick={removeAllTasks}>
            Delete All
          </button>
        </div>
      )}
    </div>
  );
};

export default Todo;
