import React, { useState } from 'react';

const Todo = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editingTaskIndex, setEditingTaskIndex] = useState(null);

  const addTask = () => {
    if (newTask.trim() !== '') {
      if (editingTaskIndex !== null) {
        // If editingTaskIndex is not null, update the existing task
        const updatedTasks = [...tasks];
        updatedTasks[editingTaskIndex].text = newTask;
        setTasks(updatedTasks);
        setEditingTaskIndex(null);
      } else {
        // If editingTaskIndex is null, add a new task
        setTasks([...tasks, { text: newTask, completed: false }]);
      }
      setNewTask('');
    }
  };

  const startEditing = (index) => {
    setEditingTaskIndex(index);
    setNewTask(tasks[index].text);
  };

  const cancelEditing = () => {
    setEditingTaskIndex(null);
    setNewTask('');
  };

  const toggleTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };

  const removeTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  const removeAllTasks = () => {
    setTasks([]);
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Todo List</h1>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Add a new task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              addTask();
            }
          }}
        />
        <button className="btn btn-primary" type="button" onClick={addTask}>
          {editingTaskIndex !== null ? 'Update' : 'Add'}
        </button>
        {editingTaskIndex !== null && (
          <button className="btn btn-secondary" type="button" onClick={cancelEditing}>
            Cancel
          </button>
        )}
      </div>
      <ul className="list-group">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.completed ? 'text-muted' : ''
            }`}
          >
            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                id={`checkbox-${index}`}
                checked={task.completed}
                onChange={() => toggleTask(index)}
              />
              {editingTaskIndex === index ? (
                <input
                  type="text"
                  className="form-control"
                  value={newTask}
                  onChange={(e) => setNewTask(e.target.value)}
                />
              ) : (
                <label className="form-check-label" htmlFor={`checkbox-${index}`}>
                  {task.completed ? <del>{task.text}</del> : task.text}
                </label>
              )}
            </div>
            <div>
              {editingTaskIndex === index ? (
                <button className="btn btn-success btn-sm" onClick={() => addTask()}>
                  Save
                </button>
              ) : (
                <button className="btn btn-warning btn-sm" onClick={() => startEditing(index)}>
                  Edit
                </button>
              )}
              <button className="btn btn-danger btn-sm" onClick={() => removeTask(index)}>
                Remove
              </button>
            </div>
          </li>
        ))}
      </ul>
      {tasks.length > 0 && (
        <div className="mt-3">
          <button className="btn btn-danger" onClick={removeAllTasks}>
            Delete All
          </button>
        </div>
      )}
    </div>
  );
};

export default Todo;
